@push('js')
<script>
    const lightsContainer = document.getElementById('lights-main');
    const ligtsRange = document.getElementById('lights');

    const lights = [
      { value: 30, svg: 'light-1', class: 'text-teal-custom' },
      { value: 60, svg: 'light-2', class: 'text-yellow-custom' },
      { value: 80, svg: 'light-3', class: 'text-red-main' },
    ];

    function updateLights() {
      const value = parseInt(ligtsRange.value);
    
      const light = lights.reduce((prev, curr) => {
        return curr.value <= value ? curr : prev;
      }, lights[0]);

      const newClass = light.class;
      const oldClass = lightsContainer.getAttribute('class');

      if(light.svg === 'light-1')
      {
        document.getElementById('light-3').style.display = 'none';
        document.getElementById('light-2').style.display = 'none';
        document.getElementById('light-1').style.display = 'block';
      } else if(light.svg === 'light-2') {
        document.getElementById('light-1').style.display = 'none';
        document.getElementById('light-3').style.display = 'none';
        document.getElementById('light-2').style.display = 'block';
      } else if(light.svg === 'light-3') {
        document.getElementById('light-1').style.display = 'none';
        document.getElementById('light-2').style.display = 'none';
        document.getElementById('light-3').style.display = 'block';
      }
     

      if(oldClass) {
        lightsContainer.classList.remove(oldClass);
      }
      lightsContainer.classList.add(newClass);
    }

    ligtsRange.addEventListener('input', updateLights);
 
    updateLights();
</script>
@endpush